import { ChainBlock } from './ChainBlock';

export class PhraseBlock {
  constructor(p, g, phrase){
    this.p = p;
    this.g = g;
    this.phrase = phrase;
    this.wordObArray = new Array();
    this.textArr = new Array();
    this.setArrByPartOfSpeech(phrase);
    if(this.textArr.length != this.wordObArray.length) console.log("要素数違うお！");
    this.phraseleng = this.textArr.length;
    //this.startTime = phrase.startTime;
    //this.endTime = phrase.endTime;
    //console.log("-------this is phraseBlocks------");
    //console.log("startTime is "+this.startTime + "endTime is "+this.endTime);
    this.wordBlocks = new Array(this.phraseleng);
    this.col = p.color(Math.floor(Math.random() * 255), Math.floor(Math.random() * 255), 255);

    this.displayChildrenCount = 0;
    this.isDisplayAll = false;
    this.makeWordBlock();
  }

  makeWordBlock() {
    const which = this.generateWhich();
    const bottom = this.generateBottom(which);
    for(let i = 0; i < this.phraseleng; i++) {
      this.wordBlocks[i] = new ChainBlock(this.p, this.g, this.wordObArray[i], this.textArr[i], this.col, which, bottom);
    }
  }

  generateWhich() {
    const ptn = ['row', 'col'];
    return ptn[ Math.floor(Math.random() * ptn.length) ];
  }

  generateBottom(which) {
    const rawPtn = ['B', 'T'];
    const colPtn = ['L', 'R'];
    if(which === 'row') {
      return rawPtn[ Math.floor(Math.random() * rawPtn.length) ];
    } else {
      return colPtn[ Math.floor(Math.random() * colPtn.length) ];
    }
  }

  display() {
    for(const w of this.wordBlocks) {
      w.display();
    }
  }

  //指定された品詞をまとめる
  setArrByPartOfSpeech(phrase) {
    const words = phrase.children;
    const textArray = new Array();
    const wordObArray = new Array();
    let i = 0;
    let test = "";
    let isA = false;
    let tempAtext = "";
    let tempAob;
    for(const word of words) {
      test += word.text;
      test += word.pos+" | ";
      let text = word.text;
      const w = new Array();
      if(isA) {
        text = tempAtext + word.text;
        w.push(tempAob);
        tempAtext = "";
        tempAob = null;
        isA = false;
      }
      w.push(word); //wordオブジェクトを配列にする
      //助詞(P),助動詞(M)のとき前の要素に追加
      if(word.pos === 'P' || word.pos === 'M') {
        textArray[i-1] += text;
        wordObArray[i-1].push(word);
      //連体詞(A)のとき後ろの要素に追加
      } else if(word.pos === 'A') {
        isA = true;
        tempAtext = word.text;
        tempAob = word;
      } else {
        textArray.push(text);
        wordObArray.push(w);
        i++;
      }

    }

    this.wordObArray = wordObArray;
    this.textArr = textArray;
    /*
    const arr = new Array();
    for(i = 0; i < textArray.length; i++) {
      const tmp = {text: textArray[i], o: wordObArray[i]};
      arr.push(tmp);
    }
    for(let i of arr) {
      console.log(i);
      console.log(i['o']);
    }
    */
  }
  get _wordBlocks() {
    return this.wordBlocks;
  }

  get _isDisplayAll() {
    return this.isDisplayAll;
  }
}
