export class SizingBlock {
  constructor(p, charOb, size, dir, posArray, parent) {
    this.p = p;
    this.charOb = charOb;

    this.globalBlockSize = 100;
    this.blockSize = size * this.globalBlockSize;
    this.char = charOb.text;
    this.posArray = posArray;
    this.posX = 0;
    this.posY = 0;
    this.calcPos();
    /*
    this.x = x;
    this.posX = (this.x * this.blockSize) + (this.blockSize / 2);
    this.y = y;
    this.posY = (this.y * this.blockSize) + (this.blockSize / 2);
    */
    this.col = p.color(100, 100, 100);
    //this.char = char;
    this.dir = dir;
    this.parent = parent;
    //console.log("-----this is Block-----");
    this.startTime = charOb.startTime;
    this.endTime = charOb.endTime;
    this.isDisplayed = false;
  }

  displayText() {
    const p = this.p;
    let rad;
    switch(this.dir) {
      case 'N':
        rad = 0;
        break;
      case 'S':
        rad = p.radians(180);
        break;
      case 'E':
        rad = p.radians(90);
        break;
      case 'W':
        rad = p.radians(-90);
        break;
    }
    p.textSize(this.blockSize);
    p.push();
    p.translate(this.posX, this.posY);
    p.rotate(rad);
    p.noStroke();
    p.fill(this.col);
    p.text(this.char, 0, 0);
    p.pop();
    this.isDisplayed = true;
  }

  calcPos() {
    if(this.posArray.length == 1) {
      const pos = this.posArray[0];
      this.posX = (pos[1] * this.globalBlockSize) + (this.blockSize / 2);
      this.posY = (pos[0] * this.globalBlockSize) + (this.blockSize / 2);
    } else {
      let x = new Array();
      let y = new Array();
      for(const a of this.posArray) {
        x.push(a[1]);
        y.push(a[0]);
      }
      const minX = Math.min(...x);
      const minY = Math.min(...y);
      this.posX = (minX * this.globalBlockSize) + (this.blockSize / 2);
      this.posY = (minY * this.globalBlockSize) + (this.blockSize / 2);
    }
  }
/*
  get X() {
    return this.x;
  }
  get Y() {
    return this.y;
  }
*/
  get _startTime() {
    return this.startTime;
  }
  get _endTime() {
    return this.endTime;
  }
  get _isDisplayed() {
    return this.isDisplayed;
  }

}
