class Camera {
  constructor(p5) {
		this.p5 = p5;
    this.pos = p5.createVector(0, 0);
		this._zoom = 0.5;
		console.log("かめっらららららら");
		console.log(this.pos);
  }
  draw(key) {
    if (key == 'w') this.pos.y -= 50;
    if (key == 's') this.pos.y += 50;
    if (key == 'a') this.pos.x -= 50;
    if (key == 'd') this.pos.x += 50;

		if (key == 'x') this._zoom *= 2;
  }
	get posX() {
		return this.pos.x;
	}
	get posY() {
		return this.pos.y;
	}
	get zoom() {
		return this._zoom;
	}
}
