class Camera {
  constructor(p5, canvasW, canvasH) {
		this.p5 = p5;
    this.pos = p5.createVector(0, 0);
		this._zoom = 0.3;
  }
  draw(key) {
    if (key == 'w') this.pos.y -= 50;
    if (key == 's') this.pos.y += 50;
    if (key == 'a') this.pos.x -= 50;
    if (key == 'd') this.pos.x += 50;
		if (key == 'x') this._zoom *= 2;
  }
	get posX() {
		return this.pos.x;
	}
	get posY() {
		return this.pos.y;
	}
	get zoom() {
		return this._zoom;
	}

	set posX(num) {
		this.pos.x += num - this.pos.x;
	}
	set posY(num) {
		this.pos.y += num - this.pos.y;
	}
}

class ZoomCamera {
	constructor(p5, canvasW, canvasH) {
		this.p = p5;
		this.canvasW = canvasW;
		this.canvasH = canvasH;
		this._x = 0;
		this._y = 0;
		this._zoom = 0;
	}

	update() {
		this._zoom = 0.5;
		this._x = (this.canvasW / 2) - window.innerWidth;
		this._y = (this.canvasH / 2) - window.innerHeight;
	}

	get x() {
		return this._x;
	}
	get y() {
		return this._y;
	}
	get zoom() {
		return this._zoom;
	}
}

